// --- Part Two ---
//
// You notice a progress bar that jumps to 50% completion.
// Apparently, the door isn't yet satisfied, but it did emit a star as encouragement. The instructions change:
//
// Now, instead of considering the next digit, it wants you to consider the digit halfway around the circular list.
// That is, if your list contains 10 items, only include a digit in your sum if the digit 10/2 = 5 steps forward matches it.
// Fortunately, your list has an even number of elements.
//
// For example:
//
// 1212 produces 6: the list contains 4 items, and all four digits match the digit 2 items ahead.
// 1221 produces 0, because every comparison is between a 1 and a 2.
// 123425 produces 4, because both 2s match each other, but no other digit has a match.
// 123123 produces 12.
// 12131415 produces 4.

function count(str) {
  var sum = 0;
  if(str && str.length > 0 && (str.length % 2 === 0)){
    var halfway = str.length/2;
    var fullStr = str + str.substr(0, halfway);
    for (var i = 0; i < fullStr.length; i++) {
      if (fullStr[i] && fullStr[halfway + i]) {
        if (fullStr[i] === fullStr[halfway + i]) {
          sum += parseInt(fullStr[i], 10);
        }
      }
    }
  }
  return sum;
}

function checkExamples(str, answer) {
  var sum = count(str);
  var correct = (sum === answer);
  console.log("str: ", str);
  console.log("answer: ", answer);
  console.log("count: ", sum);
  if (correct) {
    console.log("You got it right!");
  }
  else {
    console.log("You got it wrong.");
  }
}

var strings = ["1212", "1221", "123425", "123123", "12131415"];
var counts = [6, 0, 4, 12, 4];
for (var i = 0; i < strings.length; i++) {
  checkExamples(strings[i], counts[i]);
}

var input = "";
var answer = count(input);
console.log("The answer is: ", answer);

